var refreshBlockPositionChanger=function(elementId,distanceFromTop){var element=document.getElementById(elementId);function checkScrollState(){if(window.scrollY>distanceFromTop&&!shouldPrepareForSiteOffset()){element.style.top="0px";element.style.position="fixed"}else{if(shouldPrepareForSiteOffset()){makeAnyNecessaryChange()}else{element.style.position="relative";element.style.top="0px"}}}function makeAnyNecessaryChange(){if(navigator.platform=="MacIntel"){element.style.top="-"+getPageOffset()+"px"}}function getPageOffset(){var offset=(window.scrollY+window.innerHeight)-document.height;return offset}function shouldPrepareForSiteOffset(){if((window.scrollY+window.innerHeight)>=document.height-100){return true}return false}document.addEventListener("scroll",checkScrollState)}